var popup=document.querySelector(".modal"),tip1=popup.querySelector("#tip1"),tip2=popup.querySelector("#tip2"),tip3=popup.querySelector("#tip3"),link=document.querySelector(".btn-feedback"),close=document.querySelector(".form-feedback-exit"),form=popup.querySelector("form"),username=popup.querySelector("[name=username]"),email=popup.querySelector("[name=email]"),text=popup.querySelector("[name=text]");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-show")}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-show"),form.classList.remove("modal-error")}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))}),form.addEventListener("submit",function(a){username.value&&email.value&&text.value?(localStorage.setItem("username",username.value),localStorage.setItem("email",email.value)):(a.preventDefault(),form.classList.remove("modal-error"),form.offsetWidth=popup.offsetWidth,form.classList.add("modal-error"))}),username.addEventListener("blur",function(a){a.preventDefault(),username.value?tip1.classList.add("tip-color"):tip1.classList.remove("tip-color")}),email.addEventListener("blur",function(a){a.preventDefault(),email.value?tip2.classList.add("tip-color"):tip2.classList.remove("tip-color")}),text.addEventListener("blur",function(a){a.preventDefault(),text.value?tip3.classList.add("tip-color"):tip3.classList.remove("tip-color")});